          <div class="pick-stats-wrap">
            <div class="pick-name-type">
              <div class="pick-value pick-name">{{ item.name }}</div>
              <div class="pick-sprite">
                <img
                  :src="pokeSprite(item.id - 1, 'other', 'official-artwork')"
                  :alt="item.name"
                />
              </div>
            </div>
            <div class="pick-stats">
              <TypeImage
                class="type-fade"
                :poke="item"
